var data = [{ id: 1, author: "Toto", title: "First music" }, { id: 2, author: "titi", title: "Second music" }];

var MusicList = React.createClass({
    displayName: "MusicList",

    render: function () {
        var musicNode = this.props.data.map(function (music) {
            return React.createElement(
                "div",
                { key: music.id },
                music.title
            );
        });
        return React.createElement(
            "div",
            { className: "musicList" },
            musicNode
        );
    }
});

var Container = React.createClass({
    displayName: "Container",

    render: function () {
        return React.createElement(
            "div",
            { className: "container" },
            React.createElement(MusicList, { data: this.props.data })
        );
    }
});

ReactDOM.render(React.createElement(Container, { data: data }), document.getElementById('content'));